module _7seg_display (
    input [3:0] val_i,
    output top_left_vert_o,
    output top_right_vert_o,
    output bot_left_vert_o,
        output bot_right_vert_o,
    output top_horizontal_o,
    output mid_horizontal_o,
    output bot_horizontal_o
);

  wire zero, one, two, three, four, five, six, seven, eight, nine;
  wire A, b, C, d, E, F;

  assign zero = val_i == 4'b0000;
  assign one = val_i == 4'b0001;
  assign two = val_i == 4'b0010;
  assign three = val_i == 4'b0011;
  assign four = val_i == 4'b0100;
  assign five = val_i == 4'b0101;
  assign six = val_i == 4'b0110;
  assign seven = val_i == 4'b0111;
  assign eight = val_i == 4'b1000;
  assign nine = val_i == 4'b1001;
  assign A = val_i == 4'b1010;
  assign b = val_i == 4'b1011;
  assign C = val_i == 4'b1100;
  assign d = val_i == 4'b1101;
  assign E = val_i == 4'b1110;
  assign F = val_i ==  4'b1111;

  assign top_horizontal_o = !one && !four && !b && !d;
  assign mid_horizontal_o = !zero && !one && !seven && !C;
  assign bot_horizontal_o = !one && !four && !seven && !nine && !A && !F;
  assign top_left_vert_o  = !one && !two && !three && !seven && !d;
  assign bot_left_vert_o  = !one && !three && !four && !seven && !nine;
  assign top_right_vert_o = !five && !six && !b && !C && !E && !F;
  assign bot_right_vert_o = !two && !C && !E && !F;

endmodule
